CREATE VIEW "CADIS"."IL_Compliance_Conflicts_Register" AS 
SELECT V."ConflictRegisterIdBK" AS "Conflict Register Id", J1.DF AS "Person",V."ConflictTitle" AS "Title",V."ConflictSummary" AS "Summary",V."ConflictClosed" AS "Closed",V."ConflictInactive" AS "Inactive",V."DocumentationFolderLink" AS "Supporting Documentation",V."ConflictCreationDate" AS "Creation Date",V."ConflictLastModifiedDate" AS "Modified Date" FROM "Compliance"."ConflictsRegister" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J1 ON  J1.JF=V."NotifyingPersonId"
