CREATE VIEW "CADIS"."IL_Compliance_Conflicts_Register_Actions" AS 
SELECT V."ConflictsRegisterActionId" AS "Conflicts Register Action ID",V."ConflictId" AS "Conflict ID", J2.DF AS "Action Type ID",V."ActionDate" AS "Action Date",V."ActionComment" AS "Action Comment", J5.DF AS "Created By Person ID",V."CreationDate" AS "Creation Date",V."JIRAIssueKey",V."IsActive",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY" FROM "Compliance"."ConflictsRegisterActions" V LEFT OUTER JOIN (SELECT DISTINCT "ActionTypeId" AS JF,"ActionType" AS DF FROM "Compliance"."ConflictsRegisterActionTypes")  J2 ON  J2.JF=V."ActionTypeId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J5 ON  J5.JF=V."CreatedByPersonId"
