CREATE VIEW "CADIS"."IL_Compliance_Conflicts_Register_Identification" AS 
SELECT V."ConflictId" AS "Conflict ID", J1.DF AS "Conflicts Register Generic ID",V."ConflictIdentifier" AS "Conflict Identifier",V."ConflictIdentifierOverride" AS "Conflict Identifier Override", J4.DF AS "Conflicts Register Category ID 1", J5.DF AS "Conflicts Register Category ID 2",V."DocumentationFolderUrl" AS "Documentation Folder Url", J7.DF AS "Created By Person ID",V."CreationDate" AS "Creation Date" FROM "Compliance"."ConflictsRegisterIdentification" V LEFT OUTER JOIN (SELECT DISTINCT "ConflictsRegisterGenericId" AS JF,"GenericConflictTitle" AS DF FROM "Compliance"."ConflictsRegisterGenerics")  J1 ON  J1.JF=V."ConflictsRegisterGenericId" LEFT OUTER JOIN (SELECT DISTINCT "ConflictsRegisterCategoryId" AS JF,"ConflictsRegisterCategory" AS DF FROM "Compliance"."ConflictsRegisterCategories")  J4 ON  J4.JF=V."ConflictsRegisterCategoryId1" LEFT OUTER JOIN (SELECT DISTINCT "ConflictsRegisterCategoryId" AS JF,"ConflictsRegisterCategory" AS DF FROM "Compliance"."ConflictsRegisterCategories")  J5 ON  J5.JF=V."ConflictsRegisterCategoryId2" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J7 ON  J7.JF=V."CreatedByPersionId"
