CREATE VIEW "CADIS"."IL_Compliance_EBI_Register_User_Interactions" AS 
SELECT V."EBIRegisterUserInteractionId" AS "EBI Register User Interaction ID",V."EBIRegisterId" AS "EBI RegisterId", J2.DF AS "User Interaction Type ID",V."InteractionMessage" AS "Interaction Message",V."InteractionDate" AS "Interaction Date",V."WorkflowVersionGUID" AS "Workflow Version GUID",V."JoinGUID" AS "Join GUID" FROM "Compliance"."EBIRegisterUserInteractions" V LEFT OUTER JOIN (SELECT DISTINCT "UserInteractionTypeId" AS JF,"UserInteractionType" AS DF FROM "Compliance"."EBIRegisterUserInteractionTypes")  J2 ON  J2.JF=V."UserInteractionTypeId"
