CREATE VIEW "CADIS"."IL_Compliance_Monitoring_Themes" AS 
SELECT V."MonitoringThemeId" AS "Monitoring Theme Id", J1.DF AS "Monitoring Category",V."ThemeName" AS "Theme Name",V."ThemeSummary" AS "Theme Summary",V."SubmittedByPersonId",V."JoinGUID",V."MonitoringThemeCreationDate" AS "Monitoring Theme Creation Date",V."MonitoringThemeLastModifiedDate",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Compliance"."MonitoringThemes" V LEFT OUTER JOIN (SELECT DISTINCT "MonitoringCategoryId" AS JF,"CategoryName" AS DF FROM "Compliance"."MonitoringCategories")  J1 ON  J1.JF=V."MonitoringCategoryId"
