CREATE VIEW "CADIS"."IL_Compliance_PA_Dealing_Request_Events" AS 
SELECT V."PADealingRequestEventId" AS "PA Dealing Request Event ID",V."PADealingRequestRegisterId" AS "PA Dealing Request Register ID",V."PADealingRequestEventType" AS "PA Dealing Request Event Type", J3.DF AS "Event Person ID", J4.DF AS "Event Role ID",V."EventTrueFalse" AS "Event True False",V."EventDetails" AS "Event Details",V."EventDate" AS "Event Date",V."DocumentationFolderLink" AS "Documentation Folder Link",V."PADealingRequestEventCreationDate" AS "PA Dealing Request Event Creation Date",V."PADealingRequestEventLastModifiedDate" AS "PA Dealing Request Event Last Modified Date" FROM "Compliance"."PADealingRequestEvents" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J3 ON  J3.JF=V."EventPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J4 ON  J4.JF=V."EventRoleId"
