CREATE VIEW "CADIS"."IL_Compliance_PA_Dealing_Request_Register" AS 
SELECT V."PADealingRequestRegisterId" AS "PA Dealing Request Register ID", J1.DF AS "Requestor Person ID", J2.DF AS "Requestor Role ID",V."RequestedDate" AS "Requested Date",V."PADealingRequestStatus" AS "PA Dealing Request Status",V."OnStopList" AS "On Stop List",V."InvestmentName" AS "Investment Name",V."InvestmentType" AS "Investment Type",V."PADealingTransactionTypeBK" AS "PA Dealing Transaction Type BK",V."WoodfordInvestmentYesNo" AS "Woodford Investment Yes No",V."Value" AS "Value",V."EmployeeComments" AS "Employee Comments",V."ComplianceComments" AS "Compliance Comments",V."ComplianceDecisionDate" AS "Compliance Decision Date", J14.DF AS "Compliance Person ID",V."DocumentationFolderLink" AS "Documentation Folder Link",V."PADealingRequestCreationDatetime" AS "PA Dealing Request Creation Datetime",V."PADealingRequestLastModifiedDatetime" AS "PA Dealing Request Last Modified Datetime" FROM "Compliance"."PADealingRequestRegister" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J1 ON  J1.JF=V."RequestorPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J2 ON  J2.JF=V."RequestorRoleId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J14 ON  J14.JF=V."CompliancePersonId"
