CREATE VIEW "CADIS"."IL_Fact_KPIFact" AS 
SELECT V."KPIFactId" AS "KPI Fact ID", J1.DF AS "Measure Date ID",V."KPIId" AS "KPI ID",V."MeasureValue" AS "Measure Value",V."LastUpdatedDatetime" AS "Last Updated Datetime",V."ControlId" AS "Control ID", J6.DF AS "Source System ID",V."RecordedBy" AS "Recorded By",V."JoinGUID" AS "Join GUID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Fact"."KPIFact" V LEFT OUTER JOIN (SELECT DISTINCT "CalendarId" AS JF,"CalendarId" AS DF FROM "CADIS"."VW_Calendar_Decode_Source")  J1 ON  J1.JF=V."MeasureDateId" LEFT OUTER JOIN (SELECT DISTINCT "SourceSystemId" AS JF,"SourceSystemName" AS DF FROM "Audit"."SourceSystems")  J6 ON  J6.JF=V."SourceSystemId"
