CREATE VIEW "CADIS"."IL_Investment_FinPro_Events" AS 
SELECT V."FinPromEventId" AS "FinProm Event ID",V."FinPromRegisterId" AS "FinProm Registered ID",V."FinPromEventType" AS "FinProm Event Type", J3.DF AS "Event Person ID", J4.DF AS "Event Role ID",V."EventTrueFalse" AS "Event True False",V."EventComments" AS "Event Comments",V."EventDate" AS "Event Date",V."FinPromEventCreationDate" AS "FinProm Creation Datetime",V."FinPromEventLastModifiedDate" AS "FinProm Event Last Modified Date",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Investment"."FinPromEvents" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J3 ON  J3.JF=V."EventPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J4 ON  J4.JF=V."EventRoleId"
