CREATE VIEW "CADIS"."IL_Investment_Research_Broker_Budget_Changes" AS 
SELECT V."BudgetChangeId" AS "Budget Change Id", J1.DF AS "Research Broker",V."PerformanceChange" AS "Performance Change?",V."ResourceChange" AS "Resource Change?",V."SectorChange" AS "Sector Change?",V."BudgetChangeComments" AS "Budget Change Comments",V."ChangeAmount" AS "Change Amount",V."BudgetAmendmentDate" AS "Budget Amendment Date", J8.DF AS "Amended By Person",V."DocumentationFolderLink" AS "Documentation Folder Link",V."JoinGUID" AS "Join GUID",V."BudgetChangeCreationDatetime" AS "Budget Change Creation Datetime",V."BudgetChangeLastModifiedDatetime" AS "Budget Change Last Modified Datetime" FROM "Investment"."ResearchBrokerBudgetChanges" V LEFT OUTER JOIN (SELECT DISTINCT "ResearchBrokerId" AS JF,"BrokerCompanyName" AS DF FROM "Investment"."ResearchBrokerRegister")  J1 ON  J1.JF=V."ResearchBrokerId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J8 ON  J8.JF=V."AmendedByPersonId"
