CREATE VIEW "CADIS"."IL_KPI_Measured_KPIs" AS 
SELECT V."KPIId" AS "KPI ID",V."KPIName" AS "KPI Name",V."KPINameAlias" AS "KPI Name Alias",V."KPIDescription" AS "KPI Description",V."KPIBK" AS "KPI BK",V."GreenThresholdValue" AS "Green Threshold Value",V."AmberThresholdValue" AS "Amber Threshold Value",V."RedThresholdValue" AS "Red Threshold Value",V."TargetValue" AS "Target Value",V."Operator" AS "Operator",V."IsPercentage" AS "Is Percentage",V."IsActive" AS "Is Active",V."RefreshFrequencyId" AS "Refresh Frequency ID",V."AggrFunction" AS "Aggr Function",V."OwnerRoleId" AS "Owner Role ID",V."KPIDBBK" AS "KPI DBBK",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED",V."FUND_SHORT_NAME" AS "Fund Short Name" FROM "KPI"."MeasuredKPIs" V
