CREATE VIEW "CADIS"."IL_KPI_Risk_Category_Relationship" AS 
SELECT V."KPIId" AS "KPI ID", J1.DF AS "Risk Sub Category ID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "KPI"."KPIRiskCategoryRelationship" V LEFT OUTER JOIN (SELECT DISTINCT "SubCategoryId" AS JF,"SubCategory" AS DF FROM "Risk"."SubCategories")  J1 ON  J1.JF=V."RiskSubCategoryId"
