CREATE VIEW "CADIS"."IL_Monitor_Process_Run" AS 
SELECT V."RUNID" AS "Run ID",V."RUNSTART" AS "Run Start",V."RUNEND" AS "Run End",V."RUNSUCCESSFUL" AS "Run Successful", J5.DF AS "Launched By",V."PARENTRUNID" AS "Parent Run ID",V."TOPLEVELRUNID" AS "Top Level Run ID",V."RETURNCODE" AS "Return Code",V."RUNPARAMS",V."ITEMID",V."FAILEDRUNID",V."PROCESSTYPE" AS "Process Type",V."PROCESSNAME" AS "Process Name",V."RUNTIME" AS "Run Time",V."LASTLOGENTRY" AS "Last Log Entry",V."TIMESINCELASTENTRY" AS "Time Since Last Entry",V."RUNCOMPLETE" AS "Run Complete" FROM "CADIS"."vwProcessHistory" V LEFT OUTER JOIN (SELECT DISTINCT "OWNER" AS JF,"NAME" AS DF FROM "dbo"."T_REF_EXCEPTION_OWNER")  J5 ON  J5.JF=V."LAUNCHEDBY"
