CREATE VIEW "CADIS"."IL_Operation_Placing_Events" AS 
SELECT  J0.DF AS "Placing Event ID",V."PlacingRegisterId" AS "Placing Register ID",V."PlacingEventType" AS "Placing Event Type", J3.DF AS "Event Person ID",V."EventDetails" AS "Event Details",V."EventDate" AS "Event Date",V."EventTrueFalse" AS "Event True or False",V."JoinGUID" AS "Join GUID",V."PlacingEventCreationDatetime" AS "Placing Event Creation Datetime",V."PlacingEventLastModifiedDatetime" AS "Placing Event Last Modified",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Operation"."PlacingEvents" V LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J0 ON  J0.JF=V."PlacingEventId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J3 ON  J3.JF=V."EventPersonId"
