CREATE VIEW "CADIS"."IL_Operation_Proxy_Voting_Register" AS 
SELECT V."ProxyVotingRegisterId" AS "Proxy Voting Register ID",V."SecurityName" AS "Security Name",V."ISIN" AS "ISIN", J3.DF AS "Voter Person ID", J4.DF AS "Voter Role ID", J5.DF AS "Recorder Person ID",V."DateFiled" AS "Date Filed",V."CoveredByIVISYesNo" AS "Covered by IVIS",V."ProxyVotingCategory" AS "Proxy Voting Category",V."ProxyVotingStatus" AS "Proxy Voting Status",V."MeetingDate" AS "Meeting Date",V."DeadlineDate" AS "Deadline Date",V."SuggestedDecision" AS "Suggested Decision",V."ActualDecision" AS "Actual Decision",V."OperationsNotes" AS "Operations Notes",V."InvestmentNotes" AS "Investment Notes",V."CompletedProxyVoteYesNo" AS "Completed Proxy Vote Yes No",V."IrrevocableUndertakingYesNo" AS "Irrevocable Undertaking Yes No",V."DocumentationFolderLink" AS "Documentation Folder Link",V."JoinGUID" AS "Join GUID",V."ProxyVotingCreationDatetime" AS "Proxy Voting Creation Datetime",V."ProxyVotingLastModifiedDatetime" AS "Proxy Voting Last Modified",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED",V."VoterJiraKey" AS "Voter Jira Key" FROM "Operation"."ProxyVotingRegister" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J3 ON  J3.JF=V."VoterPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J4 ON  J4.JF=V."VoterRoleId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J5 ON  J5.JF=V."RecorderPersonId"
