CREATE VIEW "CADIS"."IL_Organisation_Meetings_Register" AS 
SELECT V."MeetingRegisterId" AS "Meeting Register ID", J1.DF AS "Meeting Type ID",V."MeetingNameBK" AS "Meeting Name BK",V."MeetingSummary" AS "Meeting Summary", J4.DF AS "Meeting Owner Role ID", J5.DF AS "Assignee Role ID",V."JIRAProjectKey" AS "JIRA Project Key",V."LagDays" AS "Lag Days",V."ActiveFlag" AS "Active Flag",V."DocumentationFolderLink" AS "Documentation Folder Link",V."MeetingRegisterCreationDatetime" AS "Meeting Register Creation Date",V."MeetingRegisterLastModifiedDatetime" AS "Meeting Register Last Modified",V."MeetingDay" FROM "Organisation"."MeetingsRegister" V LEFT OUTER JOIN (SELECT DISTINCT "MeetingTypeId" AS JF,"MeetingTypeBK" AS DF FROM "Organisation"."MeetingTypes")  J1 ON  J1.JF=V."MeetingTypeId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J4 ON  J4.JF=V."MeetingOwnerRoleId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J5 ON  J5.JF=V."AssigneeRoleId"
