CREATE VIEW "CADIS"."IL_Organisation_Policy_Theme_Events" AS 
SELECT V."PolicyThemeEventId" AS "Policy Theme Event ID",V."PolicyThemeRegisterId" AS "Policy Theme Register ID", J2.DF AS "Policy Theme Event Type ID",V."ReviewCollection" AS "Review Collection", J4.DF AS "Person ID", J5.DF AS "Role ID",V."EventDetails" AS "Event Details",V."EventDate" AS "Event Date",V."EventTrueFalse" AS "Event True or False",V."DocumentationFolderLink" AS "Documentation Folder Link",V."PolicyThemeEventCreationDatetime" AS "Policy Theme Event Creation Date",V."PolicyThemeEventLastModifiedDatetime" AS "Policy Theme Last Modified Date" FROM "Organisation"."PolicyThemeEvents" V LEFT OUTER JOIN (SELECT DISTINCT "PolicyThemeEventTypeId" AS JF,"PolicyThemeEventTypeBK" AS DF FROM "Organisation"."PolicyThemeEventTypes")  J2 ON  J2.JF=V."PolicyThemeEventTypeId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J4 ON  J4.JF=V."PersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J5 ON  J5.JF=V."RoleId"
