CREATE VIEW "CADIS"."IL_Organisation_Policy_Theme_Register" AS 
SELECT V."PolicyThemeRegisterId" AS "Policy Theme Register ID", J1.DF AS "PTP Category ID",V."PolicyThemeNameBK" AS "Policy Theme Name BK",V."PolicyThemeVersionNo" AS "Policy Theme Version No",V."PolicyThemeExpiryDate" AS "PolicyTheme Expiry Date",V."PolicyThemeDocumentStatus" AS "Policy Theme Document Status",V."ActiveFlag" AS "Active Flag",V."ChangeStatus" AS "Change Status",V."ChangeReason" AS "Change Reason",V."PolicyThemeSummary" AS "Policy Theme Summary", J10.DF AS "Policy Theme Review Frequency ID",V."DocumentationFolderLink" AS "Documentation Folder Link",V."DocumentationFolderId" AS "Documentation Folder ID",V."PolicyThemeCreationDatetime" AS "Policy Theme Creation Date",V."PolicyThemeLastModifiedDatetime" AS "Policy Theme Last Modified Date" FROM "Organisation"."PolicyThemeRegister" V LEFT OUTER JOIN (SELECT DISTINCT "PTPCategoryId" AS JF,"PTPCategoryBK" AS DF FROM "Organisation"."PolicyThemeProcedureCategories")  J1 ON  J1.JF=V."PTPCategoryId" LEFT OUTER JOIN (SELECT DISTINCT "PolicyThemeReviewFrequencyId" AS JF,"ReviewFrequencyName" AS DF FROM "Organisation"."PolicyThemeReviewFrequencies")  J10 ON  J10.JF=V."PolicyThemeReviewFrequencyId"
