CREATE VIEW "CADIS"."IL_Organisation_Project_Aspects" AS 
SELECT V."ProjectAspectId" AS "Project Aspect ID",V."ProjectRegisterId" AS "Project Register ID", J2.DF AS "Project Aspect Type ID",V."Costs" AS "Costs", J4.DF AS "Person ID", J5.DF AS "Role ID", J6.DF AS "Department ID",V."AspectDetails" AS "Aspect Details",V."AspectDate" AS "Aspect Date",V."AspectTrueFalse" AS "Aspect True or False",V."DocumentationFolderLink" AS "Documentation Folder Link",V."ProjectAspectCreationDatetime" AS "Project Aspect Creation Date",V."ProjectAspectLastModifiedDatetime" AS "Project Last Modified Date" FROM "Organisation"."ProjectAspects" V LEFT OUTER JOIN (SELECT DISTINCT "ProjectAspectTypeId" AS JF,"ProjectAspectTypeBK" AS DF FROM "Organisation"."ProjectAspectTypes")  J2 ON  J2.JF=V."ProjectAspectTypeId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J4 ON  J4.JF=V."PersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J5 ON  J5.JF=V."RoleId" LEFT OUTER JOIN (SELECT DISTINCT "DepartmentId" AS JF,"DepartmentName" AS DF FROM "Core"."Departments")  J6 ON  J6.JF=V."DepartmentId"
