CREATE VIEW "CADIS"."IL_Organisation_Projects_Register" AS 
SELECT V."ProjectRegisterId" AS "Project Register ID",V."ProjectNameBK" AS "Project Name BK", J2.DF AS "Requestor Role ID", J3.DF AS "Requestor Person ID", J4.DF AS "Project Owner Role ID", J5.DF AS "Project Owner Person ID", J6.DF AS "Oversight Role ID", J7.DF AS "Oversight Person ID",V."RequestDate" AS "Request Date",V."ProposedStartDate" AS "Proposed Start Date",V."EstimatedDuration" AS "Estimated Duration",V."EstimatedDurationUnits" AS "Estimated Duration Units",V."EstimatedDurationDays" AS "Estimated Duration Days",V."EstimatedCost" AS "Estimated Cost",V."TechnologyInvolved" AS "Technology Involved",V."ProjectApproved" AS "Project Approved",V."StrategicProject" AS "Strategic Project",V."ProjectPurpose" AS "Project Purpose",V."ProjectScope" AS "Project Scope",V."Dependences" AS "Dependences",V."ExternalResources" AS "External Resources",V."AdditionalDetails" AS "Additional Details",V."DocumentationFolderLink" AS "Documentation Folder Link",V."WorkflowVersionGUID" AS "Workflow Version GUID",V."JoinGUID" AS "Join GUID",V."ProjectCreationDate" AS "Project Creation Date",V."ProjectLastModifiedDate" AS "Project Last Modified Date" FROM "Organisation"."ProjectsRegister" V LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J2 ON  J2.JF=V."RequestorRoleId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J3 ON  J3.JF=V."RequestorPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J4 ON  J4.JF=V."ProjectOwnerRoleId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J5 ON  J5.JF=V."ProjectOwnerPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J6 ON  J6.JF=V."OversightRoleId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J7 ON  J7.JF=V."OversightPersonId"
