CREATE VIEW "CADIS"."IL_Organisation_Unquoted_Companies" AS 
SELECT V."UnquotedCompanyId" AS "Unquoted Company ID",V."UnquotedCompanyName" AS "Unquoted Company Name",V."UnquotedCompanySalesforceId" AS "Unquoted Company Salesforce ID",V."PrimaryContactSalesforceId" AS "Primary Contact Salesforce ID", J4.DF AS "Investment Analyst Person ID", J5.DF AS "Investment Manager Owner Person ID", J6.DF AS "Investment Manager Owner Role ID", J7.DF AS "Current Unquoted Company Stage",V."InitialMeetingDate" AS "Initial Meeting Date",V."Attendees" AS "Attendees",V."NextReviewDate" AS "Next Review Date",V."CompanyOverview" AS "Company Overview",V."NotesForSalesTeam" AS "Notes for Sales Team",V."CurrentPrice" AS "Current Price",V."CurrencyCode" AS "Currency Code",V."UnquotedCompanyRootFolderURL" AS "Unquoted Company Root Folder URL",V."InitialInformationFolderURL" AS "Initial Information Folder URL",V."InitialDueDiligenceFolderURL" AS "Initial Due Diligence Folder URL",V."FinalInvestmentFolderURL" AS "Final Investment Folder URL",V."InitialInvestmentRulesAssessment" AS "Initial Invetment Rules Assessment",V."JiraEpicKey" AS "JIRA Epic Key",V."JiraIssueKey" AS "Jira Issue Key",V."IsFCARegulated" AS "Is FCA Regulated",V."IsSRARegulated" AS "Is SRA Regulated",V."WorkflowVersionGUID" AS "Workflow Version GUID",V."JoinGUID" AS "Join GUID",V."UnquotedCompaniesCreationDate" AS "Unquoted Companies Creation Date",V."UnquotedCompaniesLastModifiedDate" AS "Unquoted Companies Last Modified Date",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Organisation"."UnquotedCompanies" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J4 ON  J4.JF=V."InvestmentAnalystPersonId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J5 ON  J5.JF=V."InvestmentManagerOwnerPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J6 ON  J6.JF=V."InvestmentManagerOwnerRoleId" LEFT OUTER JOIN (SELECT DISTINCT "UnquotedCompanyStage" AS JF,"UnquotedCompanyStageDescription" AS DF FROM "Organisation"."UnquotedCompanyStages")  J7 ON  J7.JF=V."CurrentUnquotedCompanyStage"
