CREATE VIEW "CADIS"."IL_Organisation_Unquoted_Company_Additional_Info" AS 
SELECT V."UnquotedCompanyId" AS "Unquoted Company ID",V."Geography" AS "Geography", J2.DF AS "Company Maturity ID",V."WIMEIFCompanyMaturityId",V."WIMPCTCompanyMaturityId",V."InvestmentDate" AS "Investment Date",V."NAVatInvestment" AS "NAV at Investment", J7.DF AS "Sector ID", J8.DF AS "Sub Sector ID",V."DevelopmentClassification" AS "Development Classification",V."DevelopmentClassificationId",V."PrimaryRelationInvestmentSource",V."SecondaryRelationEstEvergreenVentureId",V."Notes" AS "Notes",V."UnquotedCompaniesCreationDate" AS "Unquoted Companies Creation Date",V."UnquotedCompaniesLastModifiedDate" AS "Unquoted Companies Last Modified Date",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Organisation"."UnquotedCompanyAdditionalInfo" V LEFT OUTER JOIN (SELECT DISTINCT "CompanyMaturityId" AS JF,"CompanyMaturity" AS DF FROM "Core"."CompanyMaturities")  J2 ON  J2.JF=V."CompanyMaturityId" LEFT OUTER JOIN (SELECT DISTINCT "SectorId" AS JF,"Sector" AS DF FROM "Core"."Sectors")  J7 ON  J7.JF=V."SectorId" LEFT OUTER JOIN (SELECT DISTINCT "SectorId" AS JF,"Sector" AS DF FROM "Core"."Sectors")  J8 ON  J8.JF=V."SubSectorId"
