CREATE VIEW "CADIS"."IL_Organisation_Unquoted_Company_Decisions" AS 
SELECT V."UnquotedCompanyDecisionId" AS "Unquoted Company Decision ID", J1.DF AS "Unquoted Company Stage", J2.DF AS "Investment Decision Type",V."UnquotedCompanyId" AS "Unquoted Company ID", J4.DF AS "Decision By Person ID", J5.DF AS "Decision By Role ID",V."DecisionCreatedDate" AS "Decision Created Date",V."DecisionLastModifiedDate" AS "Decision Last Modified Date",V."DeferDecisionDate" AS "Defer Decision Date",V."JoinGUID" AS "Join GUID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Organisation"."UnquotedCompanyDecisions" V LEFT OUTER JOIN (SELECT DISTINCT "UnquotedCompanyStage" AS JF,"UnquotedCompanyStageDescription" AS DF FROM "Organisation"."UnquotedCompanyStages")  J1 ON  J1.JF=V."UnquotedCompanyStage" LEFT OUTER JOIN (SELECT DISTINCT "InvestmentDecisionType" AS JF,"InvestmentDecision" AS DF FROM "Organisation"."InvestmentDecisionTypes")  J2 ON  J2.JF=V."InvestmentDecisionType" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J4 ON  J4.JF=V."DecisionByPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J5 ON  J5.JF=V."DecisionByRoleId"
