CREATE VIEW "CADIS"."IL_Organisation_Unquoted_Company_Investment" AS 
SELECT V."UnquotedCompanyId" AS "Unquoted Company ID",V."InvestmentCategorisationMeetingDate" AS "Investment Categorisation Meeting Date",V."InvestmentCategory" AS "Investment Category",V."AnnualConfluenceChecklistUrl" AS "Annual Confluence Checklist URL",V."InvestmentCategorisationCreationDate" AS "Investment Categorisation Creation Date", J7.DF AS "Investment Categorisation Created By Person ID",V."InvestmentCategorisationModifiedDate" AS "Investment Categorisation Modified Date", J9.DF AS "Investment Categorisation Last Modified By Person ID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Organisation"."UnquotedCompanyInvestmentCategorisations" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J7 ON  J7.JF=V."InvestmentCategorisationCreatedByPersonId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J9 ON  J9.JF=V."InvestmentCategorisationLastModifiedByPersonId"
