CREATE VIEW "CADIS"."IL_Organisation_Unquoted_Company_Revaluation" AS 
SELECT V."UnquotedCompanyRevaluationId" AS "Unquoted Company Revaluation ID",V."UnquotedCompanyId" AS "Unquoted Company ID",V."EventType" AS "Event Type",V."EventOverview" AS "Event Overiew",V."EventDate" AS "Event Date",V."FurtherFundingAmount" AS "Further Funding Amount",V."Notes" AS "Notes",V."BoxUrl" AS "Box URL",V."PresentedToPricingCommitee" AS "Presented To Pricing Committee",V."RevaluationPreviousPrice" AS "Revaluation Previous Price",V."RevaluationPrice" AS "Revaluation Price",V."RevaluationEndDate" AS "Revaluation End Date",V."IsActive" AS "Is Active",V."Status" AS "Status",V."CurrencyCode" AS "Currency Code",V."IsComplete" AS "Is Complete",V."JiraIssueKey" AS "Jira Issue Key",V."UnquotedSecuritiesCreationDate" AS "Unquoted Securities Creation Date", J20.DF AS "Unquoted Securities Created By Person ID",V."UnquotedSecuritiesLastModifiedDate" AS "Unquoted Securites Last Modified Date", J22.DF AS "Unquoted Securities Last Modified By Person ID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY","CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Organisation"."UnquotedCompanyRevaluation" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J20 ON  J20.JF=V."UnquotedSecuritiesCreatedByPersonId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J22 ON  J22.JF=V."UnquotedSecuritiesLastModifiedByPersonId"
