CREATE VIEW "CADIS"."IL_Organisation_Unquoted_Securities_Fund_Investments" AS 
SELECT V."UnquotedSecuritiesFundInvestmentId" AS "Unquoted Securites Fund Investment ID",V."UnquotedSecuritiesId" AS "Unquoted Securities ID",V."FundCode" AS "Fund Code",V."NumberOfShares" AS "Number of Shares",V."FundAllocation" AS "Fund Allocation",V."TotalAmountInvested" AS "Total Amount Invested",V."PercentageHeldAtClosing" AS "Percentage Held At Closing",V."StampDuty" AS "Stamp Duty",V."UnquotedSecuritiesCreationDate" AS "Unquoted Securities Creation Date", J11.DF AS "Unquoted Securities Created By Person ID",V."UnquotedSecuritiesLastModifiedDate" AS "Unquoted Securites Last Modified Date", J13.DF AS "Unquoted Securities Last Modified By Person ID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Organisation"."UnquotedSecuritiesFundInvestments" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J11 ON  J11.JF=V."UnquotedSecuritiesCreatedByPersonId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J13 ON  J13.JF=V."UnquotedSecuritiesLastModifiedByPersonId"
