CREATE VIEW "CADIS"."IL_PreMaster_FX_Rate" AS 
SELECT V."FXRATE_ID" AS "FX Rate Id",V."DATE" AS "FX Date", J2.DF AS "From Currency", J3.DF AS "To Currency",V."SPOT_RATE" AS "FX Rate (Spot Rate)",V."CADIS_SYSTEM_INSERTED" AS "System Inserted",V."CADIS_SYSTEM_UPDATED" AS "System Updated",V."CADIS_SYSTEM_CHANGEDBY" AS "System Changed By",V."CADIS_SYSTEM_PRIORITY" AS "CADIS Priority",V."CADIS_SYSTEM_LASTMODIFIED",V."EX_RATE_CHG_PCT" AS "Exchange Rate Change (%)" FROM "dbo"."T_PREMASTER_FXRATE" V LEFT OUTER JOIN (SELECT DISTINCT "ISO_CCY_CD" AS JF,"ISO_CCY_CD" AS DF FROM "dbo"."T_REF_CURRENCY")  J2 ON  J2.JF=V."FROM_ISO_CURRENCY_CODE" LEFT OUTER JOIN (SELECT DISTINCT "ISO_CCY_CD" AS JF,"ISO_CCY_CD" AS DF FROM "dbo"."T_REF_CURRENCY")  J3 ON  J3.JF=V."TO_ISO_CURRENCY_CODE"
