CREATE VIEW "CADIS"."IL_Resolution_Log" AS 
SELECT V."COMPONENT_NAME" AS "Component Name",V."COMPONENT_TYPE" AS "Component Type",V."LOG_DATE" AS "Log Date",V."COMMENTS" AS "Comments",V."CADIS_SYSTEM_INSERTED" AS "System Inserted",V."CADIS_SYSTEM_UPDATED" AS "System Updated", J6.DF AS "System Changed by",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP" FROM "dbo"."T_RESOLUTION_LOG" V LEFT OUTER JOIN (SELECT DISTINCT "OWNER" AS JF,"NAME" AS DF FROM "dbo"."T_REF_EXCEPTION_OWNER")  J6 ON  J6.JF=V."CADIS_SYSTEM_CHANGEDBY"
