CREATE VIEW "CADIS"."IL_Risk_Register" AS 
SELECT V."RiskRegisterId" AS "Risk Register ID", J1.DF AS "Sub Category ID", J2.DF AS "Identified By Person ID",V."RiskEvent" AS "Risk Event",V."RiskCause" AS "Risk Cause",V."MitigationAndControls" AS "Mitigation And Controls", J6.DF AS "Gross Impact ID",V."GrossProbabilityId" AS "Gross Probability ID", J8.DF AS "Net Impact ID", J9.DF AS "Net Probability ID",V."ImpactOnICAAPYesNo" AS "Impact On ICAAP Yes No",V."MonetaryImpact" AS "Monetary Impact",V."Liklihoodfactor" AS "Liklihood Factor",V."FrequencyRating" AS "Frequency Rating",V."FinLossWorstCaseRating" AS "Fin Loss Worst Case Rating",V."FinLossBaseCaseRating" AS "Fin Loss Base Case Rating",V."ActionRequired" AS "Action Required", J17.DF AS "Action Role ID", J18.DF AS "Action Person Id",V."ActionDeadlineDate" AS "Action Deadline Date",V."ICAAPComments" AS "ICAAP Comments",V."RiskReviewed" AS "Risk Reviewed",V."RiskStatus" AS "Risk Status",V."ReviewStatus" AS "Review Status",V."RiskOccurrenceDate" AS "Risk Occurrence Date", J25.DF AS "SalesForce Third Party Service Issue ID",V."HORJiraKey" AS "HOR Jira Key",V."ROJiraKey" AS "RO Jira Key",V."RiskRegisterCreationDate" AS "Risk Register Creation Date",V."WorkflowVersionGUID" AS "Workflow Version GUID",V."JoinGUID" AS "Join GUID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Risk"."RiskRegister" V LEFT OUTER JOIN (SELECT DISTINCT "SubCategoryId" AS JF,"SubCategory" AS DF FROM "Risk"."SubCategories")  J1 ON  J1.JF=V."SubCategoryId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J2 ON  J2.JF=V."IdentifiedByPersonId" LEFT OUTER JOIN (SELECT DISTINCT "RiskImpactId" AS JF,"ImpactName" AS DF FROM "Risk"."RiskImpact")  J6 ON  J6.JF=V."GrossImpactId" LEFT OUTER JOIN (SELECT DISTINCT "RiskImpactId" AS JF,"ImpactName" AS DF FROM "Risk"."RiskImpact")  J8 ON  J8.JF=V."NetImpactId" LEFT OUTER JOIN (SELECT DISTINCT "RiskProbabilityId" AS JF,"ProbabilityName" AS DF FROM "Risk"."RiskProbability")  J9 ON  J9.JF=V."NetProbabilityId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J17 ON  J17.JF=V."ActionRoleId" LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J18 ON  J18.JF=V."ActionPersonId" LEFT OUTER JOIN (SELECT DISTINCT "SubCategoryId" AS JF,"SubCategory" AS DF FROM "Risk"."SubCategories")  J25 ON  J25.JF=V."SalesForceThirdPartyServiceIssueID"
