CREATE VIEW "CADIS"."IL_Risk_Register_Events" AS 
SELECT V."RiskRegisterEventId" AS "Risk Register Event ID",V."RiskRegisterId" AS "Risk Register ID", J3.DF AS "Risk Register Event Person ID",V."RiskRegisterEventComment" AS "Risk Register Event Comment",V."RiskRegisterEventCreationDate" AS "Risk Register Event Creation Date",V."WorkflowVersionGUID" AS "Workflow Version GUID",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Risk"."RiskRegisterEvents" V LEFT OUTER JOIN (SELECT DISTINCT "PersonId" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J3 ON  J3.JF=V."RiskRegisterEventPersonId"
