CREATE VIEW "CADIS"."IL_Risk_Sub_Category_Role_Owners" AS 
SELECT  J0.DF AS "Sub Category ID", J1.DF AS "Role ID",V."CreatedDate" AS "Created Date",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "Risk"."SubCategoryRoleOwners" V LEFT OUTER JOIN (SELECT DISTINCT "SubCategoryId" AS JF,"SubCategory" AS DF FROM "Risk"."SubCategories")  J0 ON  J0.JF=V."SubCategoryId" LEFT OUTER JOIN (SELECT DISTINCT "RoleId" AS JF,"RoleName" AS DF FROM "Core"."Roles")  J1 ON  J1.JF=V."RoleId"
