CREATE VIEW "CADIS"."IL_SLA_Process_Monitors" AS 
SELECT V."PLATFORM" AS "Platform",V."SOURCE" AS "Source",V."ENTITY" AS "Entity",V."SUB_ENTITY" AS "Sub Entity",V."RUN_DATE" AS "Run Date",V."ROW_COUNT" AS "Row Count",V."PREVIOUS_ROW_COUNT" AS "Previuos Row Count",V."PROCESS_STATUS" AS "Process Status",V."RUN_STATUS" AS "Run Status",V."TIME_STATUS",V."ALERT_SENT",V."CADIS_SYSTEM_INSERTED" AS "Inserted",V."CADIS_SYSTEM_UPDATED" AS "Updated",V."CADIS_SYSTEM_CHANGEDBY" AS "Changed By" FROM "dbo"."T_SLA_PROCESS_MONITOR" V
