CREATE VIEW "CADIS"."IL_SLA_Subscribers" AS 
SELECT V."SOURCE_SLA_SUBSCRIBER_ID" AS "SLA Subscriber ID", J1.DF AS "Data Feed", J2.DF AS "Subscriber",V."ACTIVE" AS "Active",V."CADIS_SYSTEM_INSERTED" AS "Inserted",V."CADIS_SYSTEM_UPDATED" AS "Updated Date",V."CADIS_SYSTEM_CHANGEDBY" AS "Updated By" FROM "dbo"."T_REF_SOURCE_SLA_SUBSCRIBER" V LEFT OUTER JOIN (SELECT DISTINCT "DATACHANNELID" AS JF,"DESCRIPTION" AS DF FROM "dbo"."V_REF_DATA_CHANNEL")  J1 ON  J1.JF=V."DATA_CHANNEL_ID" LEFT OUTER JOIN (SELECT DISTINCT "SUBSCRIBER_ID" AS JF,"DESCRIPTION" AS DF FROM "dbo"."V_REF_SUBSCRIBER")  J2 ON  J2.JF=V."SUBSCRIBER_ID"
