CREATE VIEW "CADIS"."IL_Sales_Force_Movements_Admin" AS 
SELECT  J1.DF AS "UK Income", J3.DF AS "Name",V."INT_VALUE" AS "Value","CADIS_SYSTEM_TIMESTAMP_1" FROM "CADIS"."VW_Sales_Force_Movements_Admin" V LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_SALES_FORCE_MOVEMENT")  J1 ON  J1.JF=V."SUB_ENTITY" LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_SALES_FORCE_MOVEMENT")  J3 ON  J3.JF=V."FIELD_VALUE"
