CREATE VIEW "CADIS"."IL_Salesforce_Override_New_Account" AS 
SELECT V."MatrixOutletId" AS "Matrix Outlet Id",V."AccountName" AS "Account Name",V."AccountFcaId" AS "Account FCA Id", J3.DF AS "Account Owner",V."BillingCity" AS "Billing City",V."BillingPostCode" AS "Billing Postcode",V."TotalOwnSales" AS "Total Own Sales",V."TotalMarketSales" AS "Total Market Sales",V."EquityIncomeOwnSales" AS "Equity Income Own Sales",V."EquityIncomeMarketSales" AS "Equity Income Market Sales",V."SpecialistSectorOwnSales" AS "Specialist Sector Own Sales",V."SpecialistSectorMarketSales" AS "Specialist Sector Market Sales",V."SalesFromDate" AS "Sales From",V."SalesToDate" AS "Sales To",V."MakeContact" AS "Make Contact",V."OverrideStatus" AS "Override Status" FROM "Sales.BP"."NewAccountOverride" V LEFT OUTER JOIN (SELECT DISTINCT "FullEmployeeBK" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J3 ON  J3.JF=V."AccountOwnerId"
