CREATE VIEW "CADIS"."IL_Salesforce_Override_Postcode" AS 
SELECT V."SfAccountId" AS "SF Account ID",V."AccountName" AS "Account Name",V."WF_PRIMARY_BUSINESS" AS "Primary Business",V."BillingCity" AS "Billing City",V."BillingPostcode" AS "Billing Postcode", J6.DF AS "Account Owner",V."OriginalAccountOwnerId",V."OverrideStatus" AS "Override Status",V."Revert",V."CADIS_SYSTEM_PRIORITY",V."CADIS_SYSTEM_TIMESTAMP",V."CADIS_SYSTEM_LASTMODIFIED" FROM "dbo"."T_SALESFORCE_POSTCODE_OVERRIDE" V LEFT OUTER JOIN (SELECT DISTINCT "FullEmployeeBK" AS JF,"PersonsName" AS DF FROM "Core"."Persons")  J6 ON  J6.JF=V."AccountOwnerId"
