CREATE VIEW "CADIS"."IL_T_BBG_RISK" AS 
SELECT  J0.DF AS "Fund",V."Sector",V."SubSector",V."Ticker",V."EndWeightPort" AS "EndWeightPort",V."EndWeightBench" AS "EndWeightBench",V."EndWeight" AS "EndWeightDelta",V."TotalReturn1DPort" AS "TotalReturnDayPort",V."TotalReturn1DBench" AS "TotalReturnDayBench",V."TotalReturn1D" AS "TotalReturnDayDelta",V."TotalReturnMTDPort",V."TotalReturnMTDBench",V."TotalReturnMTD",V."TotalReturnYTDPort",V."TotalReturnYTDBench",V."TotalReturnYTD",V."MaxDrawPort",V."MaxDrawBench",V."StdDevPort",V."StdDevBench",V."TrackErrPort",V."TotRtn1YPort",V."TotRtn1YBench",V."TotRtn1Y",V."TotRtn3MPort",V."TotRtn3MBench",V."TotRtn3M",V."RecPerMaxDrawPort",V."RecPerMaxDrawBench",V."MaxRtnPort",V."MaxRtnBench",V."MaxRelDrawPort",V."FileName",V."FILE_DATE",V."FILE_TYPE",V."CADIS_SYSTEM_INSERTED",V."CADIS_SYSTEM_UPDATED",V."CADIS_SYSTEM_CHANGEDBY",V."TotRtn3YPort",V."TotRtn3YBench",V."TotRtn3Y",V."TotRtnWTDPort",V."TotRtnWTDBench",V."TotRtnWTD",V."TotRtnQTDPort",V."TotRtnQTDBench",V."TotRtnQTD" FROM "dbo"."T_BBG_RISK_MONITORING_IN_PERFORMANCE" V LEFT OUTER JOIN (SELECT DISTINCT "Fund" AS JF,"Fund" AS DF FROM "dbo"."T_BBG_RISK_MONITORING_IN_ALLOCATION")  J0 ON  J0.JF=V."Fund"
