CREATE VIEW "CADIS"."IL_Unquoted_Investment_Funding" AS 
SELECT V."FUNDING_ID" AS "Funding  ID", J1.DF AS "Issuer", J2.DF AS "Security ID",V."TRADE_DATE" AS "Trade Date",V."SETTLEMENT_DATE" AS "Settlement Date", J5.DF AS "Funding Status",V."IS_LEGAL" AS "Legally Committed",V."IS_REPUTATIONAL" AS "Reputationally Committed", J8.DF AS "Type", J9.DF AS "Funding Sub Type", J10.DF AS "Push Back Unit",V."PUSH_BACK_MAGNITUDE" AS "Push Back Magnitude", J12.DF AS "Pull Forward Unit",V."PULL_FORWARD_MAGNITUDE" AS "Pull Forward Magnitude",V."TECH_STATUS" AS "Complete",V."JiraIssueKey" AS "Jira Issue Key",V."IssuerID" AS "Issuer ID" FROM "dbo"."T_UNQUOTED_FUNDING" V LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_UNQUOTED_ISSUER_LOOKUP")  J1 ON  J1.JF=V."ISSUER" LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_UNQUOTED_SECURITY_LOOKUP")  J2 ON  J2.JF=V."EDM_SEC_ID" LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_UNQUOTED_FUNDING_STATUS_LOOKUP")  J5 ON  J5.JF=V."FUNDING_STATUS" LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_UNQUOTED_FUNDING_TYPE_LOOKUP")  J8 ON  J8.JF=V."FUNDING_TYPE" LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_UNQUOTED_FUNDING_SUB_TYPE_LOOKUP")  J9 ON  J9.JF=V."FUNDING_SUB_TYPE" LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_UNQUOTED_FUNDING_DATE_UNIT_LOOKUP")  J10 ON  J10.JF=V."PUSH_BACK_UNIT" LEFT OUTER JOIN (SELECT DISTINCT "NAME" AS JF,"VALUE" AS DF FROM "dbo"."V_REF_UNQUOTED_FUNDING_DATE_UNIT_LOOKUP")  J12 ON  J12.JF=V."PULL_FORWARD_UNIT"
